import React, { useEffect, useState } from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import logo from '../assets/img/LOGO_no_name.svg';
import axios from 'axios';
import AdminCheck from './AdminCheck';

function NavBar() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setIsAuthenticated(false);
        return;
      }

      try {
        const response = await axios.get('http://localhost:3000/api/user/auth', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setIsAuthenticated(true);
        setUser(response.data); // Предполагается, что ответ содержит информацию о пользователе
      } catch (error) {
        setIsAuthenticated(false);
      }
    };

    checkAuth();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setIsAuthenticated(false);
    setUser(null);
    window.location.reload()
  };

  return (
    <Navbar expand="lg" style={{ color: 'white', backgroundColor: '#272727' }}>
      <Container>
        <img src={logo} width={60} className='me-2' alt="Logo" />
        <Navbar.Brand href="/" className='fs-3 fw-normal text-white text-decoration-none'>Gamee</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <Nav.Link href="/" className='fs-5 text-white'>Главная</Nav.Link>
            <Nav.Link href="/catalog" className='fs-5 text-white'>Каталог</Nav.Link>
            <Nav.Link href="/about" className='fs-5 text-white'>Контакты</Nav.Link>
          </Nav>

          <div className="d-flex ms-auto gap-3 align-items-center">
            {isAuthenticated ? (
              <>
                 <Nav.Link href="/lk" className='fs-5 text-white'>{user.login}(Личный кабинет)</Nav.Link>
                <Button variant="light" href="/cart">Корзина</Button>
                <AdminCheck>
                  <Button variant="light" href="/admin">Админ</Button>
                </AdminCheck>
                <Button variant="outline-light" onClick={handleLogout}>Выйти</Button>
              </>
            ) : (
              <>
                <Button variant="outline-light" href="/login">Авторизоваться</Button>
                <Button variant="light" href="/register">Зарегистрироваться</Button>
              </>
            )}
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
