import React, { useState } from 'react';
import './styles.css';
import { Container, Nav, Tab } from 'react-bootstrap';
import AdminGames from '../components/admin/AdminGames';
import AdminOrders from '../components/admin/AdminOrders';
import AdminCategories from '../components/admin/AdminCategories';

const Admin = () => {
    const [key, setKey] = useState('games');

    return (
        <Container>
            <h1 className="my-4 text-center text-white">Административная панель</h1>
            <Tab.Container id="admin-tabs" activeKey={key} onSelect={(k) => setKey(k)}>
                <Nav variant="tabs" className="mb-3">
                    <Nav.Item>
                        <Nav.Link eventKey="games" className="text-white">Игры</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link eventKey="orders" className="text-white">Заказы</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link eventKey="categories" className="text-white">Категории</Nav.Link>
                    </Nav.Item>
                </Nav>
                <Tab.Content>
                    <Tab.Pane eventKey="games">
                        <AdminGames />
                    </Tab.Pane>
                    <Tab.Pane eventKey="orders">
                        <AdminOrders />
                    </Tab.Pane>
                    <Tab.Pane eventKey="categories">
                        <AdminCategories />
                    </Tab.Pane>
                </Tab.Content>
            </Tab.Container>
        </Container>
    );
}

export default Admin;
