import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button, Container } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const LoginScreen = () => {
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateForm = () => {
    const newErrors = {};

    if (!login) {
      newErrors.login = 'Логин обязателен';
    }
    if (!password) {
      newErrors.password = 'Пароль обязателен';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      return;
    }

    try {
      const response = await axios.post('http://localhost:3000/api/user/login', {
        login,
        password,
      });
      localStorage.setItem('token', response.data.token); // Сохраняем токен в localStorage
      localStorage.setItem('role', response.data.role); // Сохраняем роль в localStorage
      navigate('/'); // Перенаправляем на главную страницу после успешного входа
      window.location.reload(); // Обновляем страницу
    } catch (error) {
      if (error.response && error.response.status === 404) {
        setErrors({ login: 'Неверный логин' });
      } 
      if (error.response && error.response.status === 400) {
        setErrors({ password: 'Неверный пароль' });
      } 
    }
  };

  return (
    <Container className="mt-3" style={{ width: '50%' }}>
      <h2 style={{color: 'white'}} className='text-center mt-4'>Авторизация</h2>
      <Form onSubmit={handleLogin}>
        <Form.Group controlId="formLogin">
          <Form.Label style={{color: 'white'}}>Логин</Form.Label>
          <Form.Control
            type="text"
            name="login"
            value={login}
            onChange={(e) => setLogin(e.target.value)}
            isInvalid={!!errors.login}
          />
          <Form.Control.Feedback type="invalid">
            {errors.login}
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group controlId="formPassword" className="mt-2">
          <Form.Label style={{color: 'white'}}>Пароль</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            isInvalid={!!errors.password}
          />
          <Form.Control.Feedback type="invalid">
            {errors.password}
          </Form.Control.Feedback>
        </Form.Group>

        <Button className="mt-3" style={{ width: '100%' }} variant="primary" type="submit">
          Войти
        </Button>
      </Form>
    </Container>
  );
};

export default LoginScreen;
