const express = require('express');
const router = express.Router();
const gameController = require('../controllers/gameController');
const upload = require('../multerConfig');

router.post('/games', upload.single('img'), gameController.create);
router.get('/games', gameController.getAll);
router.get('/games/:id', gameController.getOne);
router.put('/games/:id', upload.single('img'), gameController.update);
router.delete('/games/:id', gameController.delete);

module.exports = router;
